class ConnectedError extends Error {
    constructor() {
        super('Already connected to server!');
    }
}

class NotConnectedError extends Error {
    constructor() {
        super('Isn\'t connected to server!');
    }
}

class ServerDownError extends Error {
    constructor(server) {
        super(`${server.hostname}:${server.socketPort} is down.`);
    }
}

module.exports = {
    ConnectedError, NotConnectedError, ServerDownError,
};
